// 1. Capturamos los datos del formulario (Webhook)
const body = $('Webhook Registro').first().json.body;

// 2. Traemos la lista de comercios
const comercios = $("Obtener Todos los Comercios").all(); 

// 3. Funci√≥n para calcular el ID (C001, C002...)
function generarSiguienteSlug(comerciosExistentes) {
  if (comerciosExistentes.length === 0) return 'C001';
  const numeros = comerciosExistentes
    .map(c => c.json.slug)
    .filter(slug => slug && slug.startsWith('C'))
    .map(slug => parseInt(slug.substring(1)))
    .filter(num => !isNaN(num));
  if (numeros.length === 0) return 'C001';
  const maxNumero = Math.max(...numeros);
  return 'C' + (maxNumero + 1).toString().padStart(3, '0');
}

const nuevoSlug = generarSiguienteSlug(comercios);
const nuevoPin = Math.floor(1000 + Math.random() * 9000).toString();
const urlApp = `https://app.arnaldoayalaestratega.com`;

// 4. Fechas para Notion y Email
const fechaInicio = new Date();
const fechaFinDemo = new Date();
fechaFinDemo.setDate(fechaInicio.getDate() + 7);

// 5. RETORNAR EL OBJETO ACTUALIZADO
return {
  json: {
    // Datos para Supabase
    slug: nuevoSlug,
    pin: nuevoPin,
    nombre_local: body.nombre_local,
