// ============================================
// PARSEADOR CORREGIDO - Compatible con n8n Chat Widget
// ============================================
// Este c√≥digo va en el nodo "Parseador de Respuesta JSON" en n8n

const respuestaTexto = $json.response;

try {
  let textoLimpio = respuestaTexto.trim();

  // Limpiar markdown si viene envuelto en ```json
  if (textoLimpio.startsWith('```json')) {
    textoLimpio = textoLimpio.replace(/```json\n?/g, '').replace(/```\n?/g, '');
  }

  // Parsear el JSON de la respuesta del LLM
  const datosLimpios = JSON.parse(textoLimpio);

  // Extraer texto y botones
  let texto = datosLimpios.respuesta || datosLimpios.text || textoLimpio;
  let botones = datosLimpios.botones || datosLimpios.buttons || [];

  // üî• FORZAR botones de contacto si la respuesta menciona "contactar" o "hablar"
  const textoLower = texto.toLowerCase();
  if ((textoLower.includes('contactar') || textoLower.includes('hablar') || textoLower.includes('comunicar')) && botones.length === 0) {
    botones = [
      { etiqueta: "WhatsApp", url: "https://wa.me/543765384843" },
      { etiqueta: "Instagram", url: "https://instagram.com/arnaldoayalaestratega" },
      { etiqueta: "Sitio Web", url: "https://arnaldoayalaestratega.com" }
    ];
  }

  // ‚úÖ NUEVO: Convertir botones a formato Markdown DENTRO del texto
  if (botones.length > 0) {
    texto += '\n\n';
    botones.forEach(btn => {
      const etiqueta = btn.etiqueta || btn.label || 'Enlace';
      const url = btn.url;

      let emoji = 'üîó';
      const labelLower = etiqueta.toLowerCase();
      if (labelLower.includes('whatsapp')) emoji = 'üí¨';
      else if (labelLower.includes('instagram')) emoji = 'üì∏';
      else if (labelLower.includes('web') || labelLower.includes('sitio')) emoji = 'üåê';

      // Agregar enlaces en formato Markdown directamente al texto
      texto += `${emoji} [${etiqueta}](${url})\n`;
    });
  }

  // Devolver SOLO el texto con los enlaces integrados
  return {
    json: {
      output: texto
    }
  };

} catch (error) {
  console.error('Error parseando respuesta:', error);

  // Si falla el parseo, devolver la respuesta como texto plano
  return {
    json: {
      output: respuestaTexto
    }
  };
}

